
public class arithmeticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3, b=4,c ,d ,e;

		c = a + b ;
		d = a++ + b++ ;
		e = ++a + b++ ;
		System.out.println(a + " is not equal to "+ b);
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
	}

}
